// Captura a resposta do agente
let resposta = $json.text || $json.output || $json.response || $json.message;

if (!resposta) {
  resposta = JSON.stringify($json);
}

// Tenta converter a resposta para JSON
let parsed;
try {
  parsed = JSON.parse(resposta);
} catch (error) {
  const match = resposta.match(/\{[\s\S]*\}/);
  if (match) parsed = JSON.parse(match[0]);
  else parsed = { erro: "Falha ao converter resposta", raw: resposta };
}

return [{ json: parsed }];

